library(shiny)
library(pwr)

ui <- fluidPage(
    tags$head(
        tags$meta(charset = "utf-8"),  # Ensure proper UTF-8 encoding
        tags$link(rel = "stylesheet", href = "https://fonts.googleapis.com/css?family=Open+Sans&display=swap"),  # Load a web font
        tags$style(HTML("
      body { font-family: 'Open Sans', sans-serif; font-size: 20px; }
      .shiny-input-container { font-size: 16px; } /* Smaller font for input labels */
      .form-control { font-size: 20px; }
      .btn { font-size: 20px; }
      h3 { font-size: 24px; font-weight: bold; }
      .help-block { font-size: 18px; }
      #sample_size { font-size: 22px; font-weight: bold; color: #333; padding: 10px; border: 2px solid #ddd; background-color: #f9f9f9; border-radius: 5px; white-space: pre-wrap; }
      .custom-header { font-size: 20px; font-weight: bold; margin-top: 20px; }
    "))
    ),
    
    titlePanel("Analiza apriori statističke moci za jednu kategoricku varijablu"),
    
    sidebarLayout(
        sidebarPanel(
            numericInput("k", "Broj kategorija:", value = 3, min = 2),
            
            uiOutput("category_names_ui"),
            uiOutput("proportions_ui"),
            
            numericInput("power", "Snaga testa:", value = 0.8, min = 0.01, max = 1, step = 0.01),
            numericInput("sig_level", "Nivo znacajnosti (alpha):", value = 0.05, min = 0.001, max = 0.1, step = 0.001),
            
            actionButton("calculate", "Izracunaj potreban broj ispitanika")
        ),
        
        mainPanel(
            h3("Rezultati"),
            verbatimTextOutput("sample_size"),
            plotOutput("power_plot")
        )
    )
)

server <- function(input, output, session) {
    
    # UI for naming the categories
    output$category_names_ui <- renderUI({
        req(input$k)
        lapply(1:input$k, function(i) {
            textInput(inputId = paste0("cat_", i),
                      label = paste("Naziv kategorije", i),
                      value = paste("Kategorija", i))
        })
    })
    
    # UI for proportions once names are filled in
    output$proportions_ui <- renderUI({
        req(input$k)
        names_entered <- sapply(1:input$k, function(i) input[[paste0("cat_", i)]])
        if (any(is.null(names_entered) | names_entered == "")) return(NULL)
        
        tagList(
            div(class = "custom-header", "Nulta hipoteza - ocekivane proporcije:"),
            lapply(1:input$k, function(i) {
                numericInput(inputId = paste0("null_", i),
                             label = paste("Ocekivana proporcija za kategoriju", names_entered[i]),
                             value = round(1 / input$k, 2),
                             min = 0, max = 1, step = 0.01)
            }),
            div(class = "custom-header", "Alternativna hipoteza - ocekivane proporcije:"),
            lapply(1:input$k, function(i) {
                numericInput(inputId = paste0("alt_", i),
                             label = paste("Ocekivana proporcija za kategoriju", names_entered[i]),
                             value = round(1 / input$k, 2),
                             min = 0, max = 1, step = 0.01)
            })
        )
    })
    
    observeEvent(input$calculate, {
        req(input$k)
        cat_names <- sapply(1:input$k, function(i) input[[paste0("cat_", i)]])
        null_probs <- sapply(1:input$k, function(i) input[[paste0("null_", i)]])
        alt_probs <- sapply(1:input$k, function(i) input[[paste0("alt_", i)]])
        
        if (any(is.null(null_probs)) || any(is.null(alt_probs))) return()
        
        if (abs(sum(null_probs) - 1) > 0.01 || abs(sum(alt_probs) - 1) > 0.01) {
            output$sample_size <- renderPrint({
                cat("Greska: Zbir proporcija za svaku hipotezu pojedinacno mora biti 1.")
            })
            return()
        }
        
        if (input$k == 2) {
            # Switch to binomial test using pwr.p.test for two categories
            p0 <- null_probs[1]
            p1 <- alt_probs[1]
            h <- abs(2 * asin(sqrt(p1)) - 2 * asin(sqrt(p0)))
            
            result <- pwr.p.test(h = h, power = input$power, sig.level = input$sig_level)
            
            output$sample_size <- renderPrint({
                cat("Izracunata velicina efekta (h):", round(h, 4), "\n")
                cat(capture.output(print(result)), sep = "\n")
            })
            
            output$power_plot <- renderPlot({
                n_values <- seq(10, 500, by = 5)
                power_values <- sapply(n_values, function(n) {
                    pwr.p.test(h = h, sig.level = input$sig_level, n = n)$power
                })
                # Use ASCII labels and specify a generic font family
                plot(n_values, power_values, type = "l", col = "blue", lwd = 2,
                     main = "Snaga testa u zavisnosti od velicine uzorka",
                     xlab = "Velicina uzorka (N)",
                     ylab = "Snaga (Power)",
                     family = "sans")
                abline(h = input$power, lty = 2, col = "red")
            })
        } else {
            # Use chi-squared test for more than two categories
            w <- sqrt(sum((alt_probs - null_probs)^2 / null_probs))
            df <- input$k - 1
            
            result <- pwr.chisq.test(w = w, df = df, power = input$power, sig.level = input$sig_level)
            
            output$sample_size <- renderPrint({
                cat("Izracunata velicina efekta (w):", round(w, 4), "\n")
                cat(capture.output(print(result)), sep = "\n")
            })
            
            output$power_plot <- renderPlot({
                n_values <- seq(10, 500, by = 5)
                power_values <- sapply(n_values, function(n) {
                    pwr.chisq.test(w = w, df = df, sig.level = input$sig_level, N = n)$power
                })
                plot(n_values, power_values, type = "l", col = "blue", lwd = 2,
                     main = "Snaga testa u zavisnosti od velicine uzorka",
                     xlab = "Velicina uzorka (N)",
                     ylab = "Snaga (Power)",
                     family = "sans")
                abline(h = input$power, lty = 2, col = "red")
            })
        }
    })
}

shinyApp(ui = ui, server = server)
